import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const endpoint = "http://127.0.0.1:8000/api/vehiculo/";
const endpointTipo = "http://127.0.0.1:8000/api/tipoVehiculo";
const endpointModelo = "http://127.0.0.1:8000/api/modelo";
const endpointMarca = "http://127.0.0.1:8000/api/marca";

const ModVehiculo = () => {
  const [cantidad_ruedas, setCantidadRueda] = useState("");
  const [numero_chasis, setNumeroChasis] = useState("");
  const [id_tipo_vehiculo, setIdTipoVehiculo] = useState("");
  const [id_modelo, setIdModelo] = useState("");
  const [id_marca, setIdMarca] = useState("");
  const [patente, setPatente] = useState("");
  const [kilometraje, setKilometraje] = useState("");
  const [ArrayTipoVehiculo, setArrayTipoVehiculo] = useState([]);
  const [ArrayMarcas, setArrayMarcas] = useState([]);
  const [ArrayModelos, setArrayModelos] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  const modificar = async (e) => {
    e.preventDefault();
    await axios.put(`${endpoint}${id}`, {
      cantidad_ruedas: cantidad_ruedas,
      numero_chasis: numero_chasis,
      id_tipo_vehiculo: id_tipo_vehiculo,
      id_modelo: id_modelo,
      id_marca: id_marca,
      kilometraje: kilometraje,
      patente: patente,
    });
    navigate("/");
  };

  useEffect(() => {
    const getVehiculoById = async () => {
      const response = await axios.get(`${endpoint}${id}`);
      setCantidadRueda(response.data.cantidad_ruedas);
      setNumeroChasis(response.data.numero_chasis);
      setIdTipoVehiculo(response.data.id_tipo_vehiculo);
      setIdModelo(response.data.id_modelo);
      setIdMarca(response.data.id_marca);
      setPatente(response.data.patente);
      setKilometraje(response.data.kilometraje);
    };

    const getTipoVehiculoAll = async () => {
      const response = await axios.get(`${endpointTipo}`);
      setArrayTipoVehiculo(response.data);
    };
    const getMarcasAll = async () => {
      const response = await axios.get(`${endpointMarca}`);
      setArrayMarcas(response.data);
    };

    const getModelosAll = async () => {
      const response = await axios.get(`${endpointModelo}`);
      setArrayModelos(response.data);
    };
    getMarcasAll();
    getModelosAll();
    getTipoVehiculoAll();
    getVehiculoById();
  }, []);
  return (
    <div class="container">
      <h3>Modificar Vehiculo</h3>
      <form onSubmit={modificar} class="row g-3">
        <div class="col-md-4">
          <label class="form-label">
            Patente
            <input
              value={patente}
              onChange={(e) => setPatente(e.target.value)}
              type="text"
              class="form-control"
            />
          </label>
        </div>
        <div class="col-md-4">
          <label class="form-label">
            Numero de Chasis
            <input
              value={numero_chasis}
              onChange={(e) => setNumeroChasis(e.target.value)}
              type="text"
              class="form-control"
            />
          </label>
        </div>
        <div class="col-md-4">
          <label class="form-label">
            Cantidad de Ruedas
            <input
              value={cantidad_ruedas}
              onChange={(e) => setCantidadRueda(e.target.value)}
              type="number"
              class="form-control"
            />
          </label>
        </div>
        <div class="col-md-6">
          <label class="form-label">
            Kilometraje
            <input
              value={kilometraje}
              onChange={(e) => setKilometraje(e.target.value)}
              type="number"
              class="form-control"
            />
          </label>
        </div>

        <div class="col-md-6">
          <label class="form-label">
            Tipo de vehiculo
            <select
              value={id_tipo_vehiculo}
              onChange={(e) => setIdTipoVehiculo(e.target.value)}
              class="form-select mb-3"
            >
              {ArrayTipoVehiculo.map((data) => (
                <option key={data.id} value={data.id}>
                  {data.descripcion}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div class="col-md-6">
          <label class="form-label">
            Marcas
            <select
              value={id_marca}
              onChange={(e) => setIdMarca(e.target.value)}
              class="form-select mb-3"
            >
              {ArrayMarcas.map((data) => (
                <option key={data.id} value={data.id}>
                  {data.descripcion}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div class="col-md-6">
          <label class="form-label">
            Modelos
            <select
              value={id_modelo}
              onChange={(e) => setIdModelo(e.target.value)}
              class="form-select mb-3"
            >
              {ArrayModelos.map((data) => (
                <option key={data.id} value={data.id}>
                  {data.descripcion}
                </option>
              ))}
            </select>
          </label>
        </div>

        <div class="col-12">
          <button type="submit" class="btn btn-success">
            Guardar
          </button>
          <button
            onClick={() => navigate("/")}
            type="button"
            class="btn btn-danger"
          >
            Volver
          </button>
        </div>
      </form>
    </div>
  );
};

export default ModVehiculo;
