import React, { useEffect, useState } from "react";
import axios from "axios";
import { ENDPOINT } from "./../constants/api";
import { FaRegEdit, FaTrashAlt } from "react-icons/fa";
import { Link } from "react-router-dom";
import FilterComponent from "./../Filter/FilterComponent";

const frameworks = ["ReactJS", "AngularJS", "VueJS", "NodeJS", "EmberJS"];

const ShowVehiculo = () => {
  const [id_modelo, setIdModelo] = useState("");
  const [id_marca, setIdMarca] = useState("");
  const [ArrayMarcas, setArrayMarcas] = useState([]);
  const [ArrayModelos, setArrayModelos] = useState([]);
  const [vehiculos, setVehiculos] = useState([]);
  const [selectedFrameworks, setSelectedFrameworks] = useState([]);

  useEffect(() => {
    getAllVehiculo();
    getMarcasAll();
    getModelosAll();
  }, []);

  const handleSelect = (framework) => {
    const isSelected = selectedFrameworks.includes(framework);
    const newSelection = isSelected
      ? selectedFrameworks.filter((currentTech) => currentTech !== framework)
      : [...selectedFrameworks, framework];
    setSelectedFrameworks(newSelection);
  };
  const getAllVehiculo = async () => {
    const response = await axios.get(`${ENDPOINT}/vehiculo`, {
      /*params: {
        id_tipo_vehiculo: 1,
      },*/
    });
    setVehiculos(response.data);
  };

  const deleteVehiculo = async (id) => {
    await axios.delete(`${ENDPOINT}/vehiculo/${id}`);
    getAllVehiculo();
  };
  const getMarcasAll = async () => {
    const response = await axios.get(`${ENDPOINT}/marca`);
    setArrayMarcas(response.data);
  };

  const getModelosAll = async () => {
    const response = await axios.get(`${ENDPOINT}/modelo`);
    setArrayModelos(response.data);
  };

  return (
    <div>
      <div className="crud shadow-lg p-0 mb-2 mt-2 bg-body rounded">
        <div className="row ">
          <div
            className="col-xs-12 mt-1 mb-1 text-gred"
            style={{ color: "green" }}
          >
            <h2>Listado</h2>
          </div>
          {/* <div className="col-sm-4 mb-2 text-gred">
            <label>
              Marcas
              <select
                value={id_marca}
                onChange={(e) => setIdMarca(e.target.value)}
                className="form-select mb-3"
              >
                {ArrayMarcas.map((data) => (
                  <option key={data.id} value={data.id}>
                    {data.descripcion}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <div className="col-sm-4 text-gred">
            <label>
              Modelos
              <select
                value={id_modelo}
                onChange={(e) => setIdModelo(e.target.value)}
                className="form-select mb-3"
              >
                {ArrayModelos.map((data) => (
                  <option key={data.id} value={data.id}>
                    {data.descripcion}
                  </option>
                ))}
              </select>
            </label>
                </div>*/}
          <div className="col-sm-4 text-gred">
            <FilterComponent
              label="JS Frameworks"
              onSelect={() => alert(selectedFrameworks)}
            >
              <div className="frameworks-list">
                {frameworks.map((framework, index) => {
                  const isSelected = selectedFrameworks.includes(framework);
                  return (
                    <label key={index}>
                      <input
                        type="checkbox"
                        checked={isSelected}
                        onChange={() => handleSelect(framework)}
                      />
                      <span className="ml-2 text-base text-gray-500 font-heading">
                        {framework}
                      </span>
                    </label>
                  );
                })}
              </div>
            </FilterComponent>
          </div>
          <div className="col-sm-4 text-gred">
            <Link
              to="/add"
              className="btn btn-success btn-lg mt-2 mb-2 text-white"
            >
              Agregar
            </Link>
          </div>
        </div>
      </div>
      <table className="table table-striped">
        <thead className="bg-primary text-white">
          <tr>
            <th>Patente</th>
            <th>Numero Chasis</th>
            <th>Tipo de Vehiculo</th>
            <th>Cantidad de Ruedas</th>
            <th>Marca</th>
            <th>Modelo</th>
            <th>Kilometraje</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {vehiculos.map((vehiculo) => (
            <tr key={vehiculo.id}>
              <td>{vehiculo.patente}</td>
              <td>{vehiculo.numero_chasis}</td>
              <td>{vehiculo.tipo_vehiculo.descripcion}</td>
              <td>{vehiculo.cantidad_ruedas}</td>
              <td>{vehiculo.marca.descripcion}</td>
              <td>{vehiculo.modelo.descripcion}</td>
              <td>{vehiculo.kilometraje}</td>
              <td>
                <Link to={`/mod/${vehiculo.id}`} className="btn btn-warning">
                  <FaRegEdit />
                </Link>
                <button
                  onClick={() => deleteVehiculo(vehiculo.id)}
                  className="btn btn-danger"
                >
                  <FaTrashAlt />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ShowVehiculo;
